mapper.select:
        SELECT
        

--         级联表的字段
        ,dianying.dianying_name as dianyingName
        ,dianying.dianying_types as dianyingTypes
        ,dianying.dianying_photo as dianyingPhoto
        ,dianying.dianying_danyan as dianyingDanyan
        ,dianying.dianying_bianju as dianyingBianju
        ,dianying.dianying_address as dianyingAddress
        ,dianying.dianying_yuyan as dianyingYuyan
        ,dianying.dianying_shijian as dianyingShijian
        ,dianying.dianying_pianchang as dianyingPianchang
        ,dianying.dianying_old_money as dianyingOldMoney
        ,dianying.dianying_new_money as dianyingNewMoney
        ,dianying.zuowei_number as zuoweiNumber
        ,dianying.dianying_clicknum as dianyingClicknum
        ,dianying.shangxia_types as shangxiaTypes
        ,dianying.dianying_delete as dianyingDelete
        ,dianying.dianying_content as dianyingContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.new_money as newMoney
        ,yonghu.yonghu_delete as yonghuDelete

        FROM dianying_collection  a
        left JOIN dianying dianying ON a.dianying_id = dianying.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id

        
            
                and a.id in
                
                #{item}
                
            
            
                and (
                    a.dianying_id = #{params.dianyingId}
                )
            
            
                and (
                    a.yonghu_id = #{params.yonghuId}
                )
            
            
                and a.dianying_collection_types = #{params.dianyingCollectionTypes}
            
            
                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 
            
            
                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 
            

                
            
                and a.dianying_id IS NOT NULL
            
            
                and dianying.dianying_name like CONCAT('%',#{params.dianyingName},'%')
            
            
                and dianying.dianying_types = #{params.dianyingTypes}
            

            
                and dianying.dianying_danyan like CONCAT('%',#{params.dianyingDanyan},'%')
            
            
                and dianying.dianying_bianju like CONCAT('%',#{params.dianyingBianju},'%')
            
            
                and dianying.dianying_address like CONCAT('%',#{params.dianyingAddress},'%')
            
            
                and dianying.dianying_yuyan like CONCAT('%',#{params.dianyingYuyan},'%')
            
            
                and dianying.dianying_shijian like CONCAT('%',#{params.dianyingShijian},'%')
            
            
                and dianying.dianying_pianchang like CONCAT('%',#{params.dianyingPianchang},'%')
            
            
                  and dianying.dianying_old_money >= #{params.dianyingOldMoneyStart}   
            
            
                  and dianying.dianying_old_money <= #{params.dianyingOldMoneyEnd}   
            
            
                  and dianying.dianying_new_money >= #{params.dianyingNewMoneyStart}   
            
            
                  and dianying.dianying_new_money <= #{params.dianyingNewMoneyEnd}   
            
            
                  and dianying.zuowei_number >= #{params.zuoweiNumberStart}   
            
            
                  and dianying.zuowei_number <= #{params.zuoweiNumberEnd}   
            
            
                and dianying.zuowei_number = #{params.zuoweiNumber}
            
            
                  and dianying.dianying_clicknum >= #{params.dianyingClicknumStart}   
            
            
                  and dianying.dianying_clicknum <= #{params.dianyingClicknumEnd}   
            
            
                and dianying.dianying_clicknum = #{params.dianyingClicknum}
            
            
                and dianying.shangxia_types = #{params.shangxiaTypes}
            

            
                  and dianying.dianying_delete >= #{params.dianyingDeleteStart}   
            
            
                  and dianying.dianying_delete <= #{params.dianyingDeleteEnd}   
            
            
                and dianying.dianying_delete = #{params.dianyingDelete}
            
            
                and dianying.dianying_content like CONCAT('%',#{params.dianyingContent},'%')
            
                
            
                and a.yonghu_id IS NOT NULL
            
            
                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
            
            
                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
            
            
                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
            
            
                  and yonghu.new_money >= #{params.newMoneyStart}   
            
            
                  and yonghu.new_money <= #{params.newMoneyEnd}   
            
            
                  and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   
            
            
                  and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   
            
            
                and yonghu.yonghu_delete = #{params.yonghuDelete}
            
        

        order by a.${params.orderBy} desc
