mapper.select:
        SELECT
        

--         级联表的字段

        FROM dianying  a

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.dianying_name like CONCAT('%',#{params.dianyingName},'%')
            
            
                and a.dianying_types = #{params.dianyingTypes}
            
            
                and a.dianying_danyan like CONCAT('%',#{params.dianyingDanyan},'%')
            
            
                and a.dianying_bianju like CONCAT('%',#{params.dianyingBianju},'%')
            
            
                and a.dianying_address like CONCAT('%',#{params.dianyingAddress},'%')
            
            
                and a.dianying_yuyan like CONCAT('%',#{params.dianyingYuyan},'%')
            
            
                and a.dianying_shijian like CONCAT('%',#{params.dianyingShijian},'%')
            
            
                and a.dianying_pianchang like CONCAT('%',#{params.dianyingPianchang},'%')
            
            
                  and a.dianying_old_money >= #{params.dianyingOldMoneyStart}   
            
            
                  and a.dianying_old_money <= #{params.dianyingOldMoneyEnd}   
            
            
                  and a.dianying_new_money >= #{params.dianyingNewMoneyStart}   
            
            
                  and a.dianying_new_money <= #{params.dianyingNewMoneyEnd}   
            
            
                  and a.zuowei_number >= #{params.zuoweiNumberStart}   
            
            
                  and a.zuowei_number <= #{params.zuoweiNumberEnd}   
            
             
                and a.zuowei_number = #{params.zuoweiNumber}
             
            
                  and a.dianying_clicknum >= #{params.dianyingClicknumStart}   
            
            
                  and a.dianying_clicknum <= #{params.dianyingClicknumEnd}   
            
             
                and a.dianying_clicknum = #{params.dianyingClicknum}
             
            
                and a.shangxia_types = #{params.shangxiaTypes}
            
            
                  and a.dianying_delete >= #{params.dianyingDeleteStart}   
            
            
                  and a.dianying_delete <= #{params.dianyingDeleteEnd}   
            
             
                and a.dianying_delete = #{params.dianyingDelete}
             
            
                and a.dianying_content like CONCAT('%',#{params.dianyingContent},'%')
            

        

        order by a.${params.orderBy} desc
